  const [SttComp, SetComp] = React.useState<Trender1>(null);

  const getErr = err => {
    const err2 = JSON.stringify(err.message);
    SetComp(() => <Text>{err2}</Text>);
  };

  React.useEffect(() => {
    // ---------- set WEB Component
    if (condWeb) SetComp(<WebMap pass={props.pass} args={props.args} />);

    // ---------- set Native Component
