export const getOrderData = (): (() => Torder) => {
  let newData = getCtData('mock.B4.currData.orderData');
  let customer = getCtData('all.userData');

  // ---------------------------
  // ------ set CREDIT CARD DATA
  // ---------------------------
  newData.payments[0].credit_card.card.exp_month = Number(
    newData.payments[0].credit_card.card.exp_month,
  );
  newData.payments[0].credit_card.card.exp_year = Number(
    newData.payments[0].credit_card.card.exp_year,
  );

  // ---------------------------
  // ----- set Billing Address
  // --------------------------
  const path1 = customer.userAddress.address_components;
  if (path1[0]?.short_name)
    newData.payments[0].credit_card.card.billing_address.line_1 =
      path1[0].short_name;

  if (path1[2]?.short_name)
    newData.payments[0].credit_card.card.billing_address.city =
      path1[2].short_name;

  if (path1[3]?.short_name)
    newData.payments[0].credit_card.card.billing_address.state =
      path1[3].short_name;

  if (path1[4]?.short_name)
    newData.payments[0].credit_card.card.billing_address.country =
      path1[4].short_name;

  if (path1[5]?.short_name)
    newData.payments[0].credit_card.card.billing_address.zip_code =
      path1[5].short_name;

  return newData;
};

export type Titems = [
  {
    amount: number;
    description: string;
    quantity: number;
    code: number;
  },
];

export type Tcustomer = {
  name: string;
  type: string;
  email: string;
  document: string;
  document_type: string;
};

export type TpayOptions = 'credit_card';

export type TPayments = [
  {
    payment_method: TpayOptions;
    credit_card: {
      recurrence: boolean;
      installments: number;

      card: {
        number: string; // Entre 13 e 19 caracteres
        holder_name: string; // Máximo de 64 caracteres (Caracteres especiais e números não são aceitos)
        holder_document?: string; // CPF ou CNPJ do portador do cartão. Obrigatório caso o tipo do cartão seja voucher (bandeiras VR ou Sodexo).
        exp_month: number; // Mês de validade do cartão. Valor entre 1 e 12 (inclusive)
        exp_year: number; // Ano de validade do cartão. Formatos yy ou yyyy. Ex: 23 ou 2023.
        cvv: string; // Código de segurança do cartão. O campo aceita 4 ou 3 caracteres, variando por bandeira.

        // (Opcional) Bandeira do cartão. Para cartões de crédito, temos como valores possíveis: Elo, Mastercard, Visa, Amex, ou Hipercard. Para voucher, temos como valores possíveis: Alelo, Ticket, VR ou Sodexo.
        // brand?: string;
        // label?: string; // Indica a label do cartão

        billing_address: {
          line_1: string; // Linha 1 do endereço. (Número, Rua, e Bairro - Nesta ordem e separados por vírgula) Max: 256 caracteres.

          zip_code: string; // CEP. Max: 16 caracteres.
          city: string; // Cidade. Max: 64 caracteres.
          state: string; // Código do estado no formato ISO 3166-2.
          country: string; //Código do país no formato ISO 3166-1 alpha-2.
        };
      };
    };
  },
];

